function MongoWrapper(mongoSettings){
    assert.notEqual(mongoSettings, null);
    this.hosts = mongoSettings.hosts;
    this.db = mongoSettings.db;
    this.user = mongoSettings.user;
    this.password = mongoSettings.password;
    this.replicaSet = mongoSettings.replicaSet;
}

